import React, {useState} from 'react'
import './socialMedia.css'

export default function SocialMedia() {
 const [state , setState] = useState('#66FCF1');
 const listenScrollEvent = (e) => {
   if (window.scrollY > 400) {
     this.setState({color: '#fff'})
   } else {
     this.setState({color: '#66FCF1'})
   }
 }

  return (
    <>
    <div className="social-container">
       <ul>
           <li><a href="/" style={{color: this.state.color}} target='_blank'><i className="bi bi-linkedin"></i></a></li>
           <li><a href="/" style={{color: this.state.color}} target='_blank'><i className="bi bi-instagram"></i></a></li>
           <li><a href="/" style={{color: this.state.color}} target='_blank'><i className="bi bi-facebook"></i></a></li>
       </ul>
    </div>
    </>
  )
}
