import React, {useState} from 'react'
import PropTypes from 'prop-types'

export default function TextForm(props) {
    const [text, updateText] = useState("Enter text here...");
    return (
        <div>
            <h1>{props.heading} </h1>
            <div className="mb-3">
                <textarea value={text} className="form-control" placeholder="Leave a comment here" id="floatingTextarea2"  rows="8"></textarea>
                <button className="btn btn-primary my-4" rows="">Uppercase</button>
            </div>
        </div>
    )
}
TextForm.propTypes = {
    heading: PropTypes.string
}
TextForm.defaultProps = {
    heading: 'Heading Text'
}
