import React, {useState} from 'react'
import PropTypes from 'prop-types'

export default function TextForm(props) {
    const handleOnChange = (event) =>{
        setText(event.target.value)
    }
    const handleToUppercase = () =>{
        console.log("Your text has been converted to Uppercase successfully.")
        const newText = text.toUpperCase();
        setText(newText);
    }
    const handleToLowercase = () =>{
        console.log("Your text has been converted to Uppercase successfully.")
        const newText = text.toLowerCase();
        setText(newText);
    }
    const [text, setText] = useState("");
    return (
        <>
        <section>
            <h1>{props.heading} </h1>
            <div className="mb-3">
                <textarea value={text} onChange={handleOnChange}  placeholder="Leave a comment here" id="Mytextarea"  rows="8"></textarea>
                <button onClick={handleToUppercase} className="Button" rows="">Uppercase</button>
                <button onClick={handleToLowercase} className="Button" rows="">Lowercase</button>
            </div>
        </section>
        </>
    )
}
TextForm.propTypes = {
    heading: PropTypes.string
}
TextForm.defaultProps = {
    heading: 'Heading Text'
}
