 import React, {useState} from 'react'
 import './socialMedia.css'

 export default function SocialMedia() {
  const [state , setState] = useState('#66FCF1');
  const listenScrollEvent = (e) => {
    if (window.scrollY > 400) {
      setState('#fff')
    } else {
      setState('#66FCF1')
    }
  }
const scrollEvent = (e) => {
  window.addEventListener('scroll',listenScrollEvent)
}
   return (
     <>
     <div className="social-container">
        <ul>
            <li><a href="/" style={{color: state}} target='_blank'><i className="bi bi-linkedin"></i></a></li>
            <li><a href="/" style={{color: state}} target='_blank'><i className="bi bi-instagram"></i></a></li>
            <li><a href="/" style={{color: state}} target='_blank'><i className="bi bi-facebook"></i></a></li>
        </ul>
     </div>
     </>
   )
 }
