import React, {useState} from 'react'
import PropTypes from 'prop-types'
import './TextForm.css'

export default function TextForm(props) {
    const handleOnChange = (event) =>{
        setText(event.target.value)
    }
    const handleToUppercase = () =>{
        console.log("Your text has been converted to Uppercase successfully.")
        const newText = text.toUpperCase();
        setText(newText);
    }
    const copyToClipboard = () =>{
        const newText = document.getElementById("Mytextarea")
        newText.select()
        newText.setSelectionRange(0,-1)
        navigator.clipboard.writeText(newText.value)
    }
    const clearText = () =>{
        const newText = "";
        setText(newText)

    }
    const handleToLowercase = () =>{
        console.log("Your text has been converted to Uppercase successfully.")
        const newText = text.toLowerCase();
        setText(newText);
    }
    const [text, setText] = useState("");
    return (
        <>
        <section className='container1'>
            <h1>{props.heading} </h1>
            <div className="text-area-container">
                <textarea value={text} onChange={handleOnChange} placeholder="Leave a comment here" id="Mytextarea" ></textarea>
            </div>
            <div className="b-container">
            <button onClick={handleToUppercase} className="Button" rows="">Uppercase</button>
            <button onClick={handleToLowercase} className="Button" rows="">Lowercase</button>
            <button onClick={copyToClipboard} className="Button" rows="">Copy</button>
            <button onClick={clearText} className="Button" rows="">Clear</button>
            </div>
            <div className="r-container">
            <button onClick={handleToLowercase} className="Button" rows="">Remove Extra Space</button>
            </div>
            <div className="wc-container">
                <div className="w">
                    <h3>Words</h3>
                    <p>{text === '' ? text.split(" ").length -1 :text.split(" ").length &&  text.charAt(text.length - 1) === " " ? text.length-1: text.length}</p>
                </div>
                <div className="c">
                    <h3>Characters</h3>
                    <p>{text.length}</p>
                </div>
            </div>
        </section>
        </>
    )
}
TextForm.propTypes = {
    heading: PropTypes.string
}
TextForm.defaultProps = {
    heading: 'Heading Text'
}
